import React, { useState, useEffect } from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import styles from "./ImageCropperPreview.module.css";
import cn from "classnames";
import {
  selectUrl,
  selectImage,
  handleRemove,
  handleOpen,
  selectLoading,
  onImageLoad, offImageLoad
} from "../ImageCropper.slice";

const ImageCropperPreview = ({ className, classImage, classMenu }) => {
  const [imageLoad, setImageLoad] = useState(true);
  const url = useSelector(selectUrl);
  const image = useSelector(selectImage);
  const loading = useSelector(selectLoading);
  const dispatch = useDispatch();
  const onRemove = () => dispatch(handleRemove());

  const onChange = () => {
    dispatch(handleOpen(image));
    // onRemove();
    // setImageLoad(true);
  };

  const onLoad = () => {
    setImageLoad(false);
  };

  useEffect(() => {
    if (loading) {
      setImageLoad(true);
    }
  }, [loading]);

  return (
    <>
      {url || loading ? (
        <div className={cn(styles.preview, className)}>
          <div
            className={cn(styles.spinner, {
              [styles.hidden]: !imageLoad || !loading,
            })}
          >
            <Spinner animation="border" variant="primary" />
          </div>
          <img
            onLoad={onLoad}
            className={cn(styles.image, classImage, {
              [styles.hidden]: imageLoad || loading,
            })}
            src={url}
            alt="preview"
          />
          <div className={cn(styles.menu, classMenu)}>
            <Button
              onClick={onChange}
              size="sm"
              className="mr-2"
              disabled={imageLoad}
            >
              Изменить
            </Button>
            <Button
              onClick={onRemove}
              size="sm"
              className="mr-2"
              variant="danger"
              disabled={imageLoad}
            >
              Удалить
            </Button>
          </div>
        </div>
      ) : null}
    </>
  );
};
export default ImageCropperPreview;
