import { isEqual } from '.';
export function set(item, tree, key, value) {
    return tree.map((branch) => {
        if (isEqual(item, branch)) {
            console.log(item, key, value,  branch[key])
            branch[key] = value;
        } else if (branch.products && branch.products.length) {
            branch.products = set(item, branch.products, key, value);
        }
        return branch;
    });
}
