import React, { useRef, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";
import styles from "./PopupCropper.module.css";
import cn from "classnames";
import { Popup } from "../";
import { Button } from "react-bootstrap";
import * as cropperSlice from "./PopupCropper.slice";

const style = {
  width: 750,
  height: 470,
};

const options = {
  aspectRatio: 75 / 47,
  autoCropArea: 1,
  style,
};

export const PopupCropper = () => {
  const [ cropper, setCropper ] = useState();

  const cropperRef = useRef(null);
  const dispatch = useDispatch();
  const state = useSelector(cropperSlice.selectState);
  const image = useSelector(cropperSlice.selectImage);

  const getCropData = () => {
    if (typeof cropper !== "undefined") {
      c
      const croppImage = cropper.getCroppedCanvas().toDataURL();
      const formData = new FormData();
      const blob = new Blob([croppImage], { type: "image/png" });
      formData.append("image", blob);
    }
  };

  const onClose = () => {
    dispatch(cropperSlice.handleClose());
  };

  return (
    <Popup className={styles.popup} classContainer={styles.container} opened={state}>
      <Cropper
        src={image || ''}
        {...options}
        ref={cropperRef}
        onInitialized={(instance) => {
          setCropper(instance);
        }}
      />
      <div className={styles.buttons}>
        <Button onClick={getCropData}>Добавить</Button>
        <Button onClick={onClose} variant="secondary">Отмена</Button>
      </div>
    </Popup>
  );
};
