import React, { useState, useEffect } from 'react';
import { Popup, Steps } from '..';
import styles from './PopupProduct.module.css';
import { useSelector, useDispatch } from "react-redux";
import { useForm, Controller } from "react-hook-form";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import {
  selectState,
  handleClose,
  selectData
} from "./PopupProductSlice";

import { OneStep, TwoStep, ThreeStep } from './Steps';

export const PopupProduct = () => {
    console.log('render PopupProduct');
    const [dataForm, setDataForm] = useState({});
    const state = useSelector(selectState);
    const data = useSelector(selectData);
    const dispatch = useDispatch();

    const {
        register,
        handleSubmit,
        formState: { errors },
        reset,
      } = useForm();


    const steps = [
        { value: 1 },
        { value: 2 },
        { value: 3 },
    ];
    const onSubmit = () => {

    };

    const onClose = () => {
        setDataForm({});
        dispatch(handleClose());
    };

    useEffect(() => {
        setDataForm(data);
    }, [data]);

    useEffect(() => {
        reset(data)
    }, [reset, data]);

    return (
        <Popup opened={state} title="Добавить продукт" onClose={onClose} classContainer={styles.popup}>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <Form.Group>
                <Form.Label>Имя</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Введите название"
                    {...register("name", { required: true })}
                />
                    <Form.Text className={styles.errors}>
                        {errors.name && "Введите название"}
                    </Form.Text>
                </Form.Group>
                <Form.Group controlId="exampleForm.active">
                    <Form.Check
                        type="checkbox"
                        label="Активный"
                        {...register("active")}
                    />
                </Form.Group>
                <Form.Group controlId="exampleForm.favorites">
                    <Form.Check
                        type="checkbox"
                        label="Избранное"
                        {...register("favorites")}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Кухня</Form.Label>
                    <Form.Control
                        as="select"
                        multiple
                        {...register("kitchen", { required: true })}
                    >
                        <option value="0">Русская кухня</option>
                        <option value="1">Итальянская кухня</option>
                        <option value="2">Японская кухня</option>
                        <option value="3">Паназиатская кухня</option>
                        <option value="4">Авторская кухня</option>
                    </Form.Control>
                    <Form.Text className={styles.errors}>
                        {errors.kitchen && "Выберите кухню"}
                    </Form.Text>
                </Form.Group>
            </Form>
        </Popup>
    );
};
