import React from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import styles from "./ImageCropperPreview.module.css";
import cn from 'classnames';
import { selectUrl, selectImage, handleRemove, handleOpen, selectLoading} from "../ImageCropper.slice";

const ImageCropperPreview = ({ className, classImage, classMenu }) => {
  const url = useSelector(selectUrl);
  const image = useSelector(selectImage);
  const loading = useSelector(selectLoading);
  const dispatch = useDispatch();
  const onRemove = () => dispatch(handleRemove());

  const onChange = () => {
    dispatch(handleOpen(image));
    onRemove();
  };

  return (
    <>
     {(url) ? 
        <div className={cn(styles.preview, className)} >
            <div className={cn(styles.spinner, {
              [styles.hidden]: loading,
            })}>
              <Spinner animation="border" variant="primary" />
            </div>
            <img onLoad={onLoading} className={cn(styles.image, classImage, {
              [styles.hidden]: !loading,
            })} src={url} alt="preview" />
            <div className={cn(styles.menu, classMenu)}>
                <Button onClick={onChange} size="sm" className="mr-2" disabled={!loading}>
                  Изменить
                </Button>
                <Button onClick={onRemove} size="sm" className="mr-2" variant="danger" disabled={!loading}>
                  Удалить
                </Button>
            </div>
        </div>
        : null}
    </>
  );
};
export default ImageCropperPreview;
