import React, { memo, useState } from 'react';
import { useForm, Controller } from "react-hook-form";
import { Popup, Steps, Step } from '../';
import styles from './ProductСreate.module.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Select from 'react-select';
import validator from 'validator';
import cn from 'classnames';

import { OneStep, TwoStep } from './Steps';

export const ProductСreate = memo(({ className, opened }) => {
    const [step, setStep] = useState(0);
    const { register, handleSubmit, formState: { errors }, control } = useForm();
    const onSubmit = data => console.log(data);

    const options = [
        { value: 'chocolate', label: 'Chocolate' },
        { value: 'strawberry', label: 'Strawberry' },
        { value: 'vanilla', label: 'Vanilla' }
    ];

    const steps = [
        { value: 1 },
        { value: 2 },
        { value: 3 },
    ];

    const nextStep = () => {
        if (steps.length === step) return; 
        setStep(step+1);
    };

    const backStep = () => {
        if (step === 0) return; 
        setStep(step-1);
    };

    const test = () => {

    }

    React.useEffect(() => {
        register('price', {
          validate: (value) => validator.isCurrency(value),
        });
        register('discount_price', {
            validate: (value) => value.length === 0 || validator.isCurrency(value),
          });
    }, [register]);

    return (
        <Popup opened={opened} title="Добавить продукт">
            <Steps className={styles.steps} steps={steps} step={step} />
            <OneStep step={step} onSubmit={test} />
            <TwoStep step={step} />
            <Form onSubmit={handleSubmit(onSubmit)}>
                <Step active={step === 2}>
                    <Form.Group>
                        <Form.Label>Ингредиенты</Form.Label>
                        <Form.Control as="textarea" rows={3} {...register('ingredients')} />
                    </Form.Group>
                    <Form.Group>
                        <Form.File id="exampleFormControlFile1" label="Картинка" {...register('image')} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Вес</Form.Label>
                        <Form.Control type="text" placeholder="Введите вес" {...register('weight')} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Калорийность</Form.Label>
                        <Form.Control type="text" placeholder="Введите калорийность" {...register('calorie')} />
                    </Form.Group>
                    <div className={styles.buttons}>
                        <Button onClick={backStep} variant="secondary" type="button">Назад</Button>
                        <Button variant="primary" type="submit">
                            Добавить
                        </Button>
                    </div>
                </Step>
            </Form>
        </Popup>
    );
});
