import React, { createRef, useEffect, useMemo, useState } from 'react';
import styles from'./Tree.module.css';
import { Branch } from '../';
import cn from 'classnames';
import { productsTree } from '../../utils';

export const Tree = ({ tree, view='open', className }) => {
    const [products, setProducts] = useState([]);


    const treeElement = createRef(null);

    const transfotm = useMemo(() => productsTree(products), [products])

    function getKey({ category_id, product_id, parent_id, }) {
        if (!parent_id) {
            return category_id
        } 
        if (!product_id) {
            return category_id;
        }
        return product_id;
    }

    const handleStatus = (product) => {
        console.log(product);
    }

    useEffect(() => {
        console.log()
        setProducts(tree);
    }, [tree])

    useEffect(() => {
        window.$(treeElement.current).sortable({ 
            // fallbackOnBody: false,
            group: 'list',
            animation: 200,
            ghostClass: 'ghost',
            handle: '.position',
        });
    }, [treeElement]);

    return (
        <ul ref={treeElement} className={cn(styles.tree, className, 'list-group m-4', {
            [styles.open]: view === 'open'
        })}>
            {transfotm && transfotm.map((branch) => (
                <Branch key={getKey(branch)} product={branch} handleStatus={handleStatus} />
            ))}
        </ul>
    )
}
