import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isOpen: false,
  image: null,
};

export const popupCropperSlice = createSlice({
  name: "popupCropper",
  initialState,
  reducers: {
    handleClose: (state) => {
      state.isOpen = false;
      state.image = null;
    },
    handleOpen: (state, action) => {
      state.image = action.payload;
      state.isOpen = true;
    },
    setData: (state, action) => {
      state.data = {
        ...state.data,
        ...action.payload,
      }
    },
  },
});

export const { handleClose, handleOpen, setData } = popupCropperSlice.actions;

// The function below is called a selector and allows us to select a value from
// the state. Selectors can also be defined inline where they're used instead of
// in the slice file. For example: `useSelector((state: RootState) => state.counter.value)`
export const selectState = (state) => state.popupCropper.isOpen;
export const selectData = (state) => state.popupCropper.image;

export default popupCropperSlice.reducer;
