import { isEqual, isDishes } from '.';
export function set(item, tree, key, value) {
    return tree.map((branch) => {
        if (isEqual(branch, item)) {
            branch = value;
        } else if (isDishes(branch)) {
            branch.products = set(item, branch.products, key, value);
        }
        return branch;
    });
}
