import React, { useState, useMemo, useEffect, useCallback } from 'react';
import { Tree } from '../'
import { 
    testProductArray,
} from '../../utils';
import { dishes } from '../../module';

export const App = () => {
    const [products, setProducts] = useState(dishes.flatten(testProductArray));
    const [list, setList] = useState(dishes.tree(testProductArray, list));

    const tree = useMemo(() => {
        return dishes.tree(products, list);
    }, [ products, list ]);

    const handleStatus = (product) => {
        setProducts(dishes.setStatus(product, products, !product.active));
    }

    const handleExpand = useCallback((product) => {
        const arr = dishes.set(product, products, 'expanded', !product.expanded);
        console.log(arr.find((i) => i.name === product.name).expanded)
        setProducts(arr);
    }, [products])

    useEffect(() => {
        const startList = dishes.flatten(testProductArray);
        const startProducts = dishes.tree(testProductArray, list);
        setList(startList);
        setProducts(startProducts);
    }, []);

    return (
        <>  
            {products && products.length ? 
                <Tree 
                    tree={products} 
                    handleStatus={handleStatus}
                    handleExpand={handleExpand}
                    expanded={true}
                /> : 
                null
            }
        </>
    )
}
