export function flattenOld(products, arr=[]) {
    for (let product of products) {
        arr.push(product);
        if (Array.isArray(product.products)) { 
            flatten(product.products, arr)
        }
    }
    return arr;
}
export function flatten(products, parent_id, arr=[]) {
    for (let product of products) {
        if (product.product_id) {
            Object.defineProperty(BaseClass, 'name', {
                writable: true,
                value: 'Foo'
            });
            try {
                product.parent_id = parent_id;
            } catch(e) {
                console.log('error start')
                console.log(e)
                console.log('error end')
                console.log('parent_id start')
                console.log(parent_id)
                console.log('parent_id end')
                console.log('product start')
                console.log(product)
                console.log('product end')
            }

        }
        if (product.category_id) {
            flatten(product.products, product.category_id, arr)
            flatten(product.children, product.category_id, arr)
        }
        arr.push(product);
    }
    return arr;
}
