export function flatten(tree, parent_id, arr=[]) {
    for (let branch of tree) {
        const obj = {
            ...branch
        }
        if (obj.product_id) {
            obj.parent_id = parent_id;
        }
        if (obj.category_id) {
            flatten(obj.products, product.category_id, arr)
            flatten(product.children, product.category_id, arr)
        }
        arr.push(obj);
    }
    return arr;
}
