import React, { memo } from 'react';
import { useForm, Controller } from "react-hook-form";
import { Popup } from '../';
import styles from './ProductСreate.module.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Select from 'react-select';
import validator from 'validator';
import cn from 'classnames';

export const ProductСreate = memo(({ className, opened }) => {
    const [step , setStep] = useState(0);
    const { register, handleSubmit, formState: { errors }, control } = useForm();
    const onSubmit = data => console.log(data);
    const options = [
        { value: 'chocolate', label: 'Chocolate' },
        { value: 'strawberry', label: 'Strawberry' },
        { value: 'vanilla', label: 'Vanilla' }
    ];

    React.useEffect(() => {
        register('price', {
          validate: (value) => validator.isCurrency(value),
        });
        register('discount_price', {
            validate: (value) => value.length === 0 || validator.isCurrency(value),
          });
    }, [register]);

    return (
        <Popup opened={opened} title="Добавить продукт">
            <Form onSubmit={handleSubmit(onSubmit)}>
                <ul className={styles.steps}>
                    <li className={cn(styles.step, {
                        [styles.active]: step === 0,
                    })}>

                    </li>
                </ul>
                <Form.Group>
                    <Form.Label>Имя</Form.Label>
                    <Form.Control type="text" placeholder="Введите название" {...register("name", { required: true })} />
                    <Form.Text className={styles.errors}>
                        {errors.name && 'Введите название'}
                    </Form.Text>
                </Form.Group>
                <Form.Group controlId="exampleForm.active">
                    <Form.Check 
                        type="checkbox"
                        label="Активный"
                        {...register('active')}
                    />
                </Form.Group>
                <Form.Group controlId="exampleForm.favorites">
                    <Form.Check 
                        type="checkbox"
                        label="Избранное"
                        {...register('favorites')}
                    />
                </Form.Group>

                <Form.Group>
                    <Form.Label>Категория</Form.Label>
                    <Controller
                        name="category"
                        control={control}
                        rules={{ required: true }}
                        render={({ field }) => <Select 
                            {...field} 
                            placeholder="Выберите категорию..."
                            options={options} 
                        />}
                    />
                    <Form.Text className={styles.errors}>
                        {errors.category && 'Выберите название'}
                    </Form.Text>
                </Form.Group>

                <Form.Group>
                    <Form.Label>Кухня</Form.Label>
                    <Form.Control as="select" multiple {...register('kitchen', { required: true })}>
                        <option value="0">Русская кухня</option>
                        <option value="1">Итальянская кухня</option>
                        <option value="2">Японская кухня</option>
                        <option value="3">Паназиатская кухня</option>
                        <option value="4">Авторская кухня</option>
                    </Form.Control>
                    <Form.Text className={styles.errors}>
                        {errors.kitchen && 'Выберите кухню'}
                    </Form.Text>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Цена</Form.Label>
                    <InputGroup className="mb-3">
                        <FormControl
                            placeholder="Введите стоимость"
                            aria-label="Recipient's username"
                            aria-describedby="basic-addon2"
                            {...register('price', { required: true})}
                        />
                        <InputGroup.Append>
                            <InputGroup.Text id="basic-addon2">руб.</InputGroup.Text>
                        </InputGroup.Append>
                    </InputGroup>
                    <Form.Text className={styles.errors}>
                        {errors.price && 'Укажите стоимость'}
                    </Form.Text>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Цена со скидкой</Form.Label>
                    <InputGroup className="mb-3">
                        <FormControl
                            placeholder="Введите стоимость"
                            aria-label="Recipient's username"
                            aria-describedby="basic-addon2"
                            {...register('discount_price')}
                        />
                        <InputGroup.Append>
                            <InputGroup.Text id="basic-addon2">руб.</InputGroup.Text>
                        </InputGroup.Append>
                    </InputGroup>
                    <Form.Text className={styles.errors}>
                        {errors.discount_price && 'Укажите в правильном формате'}
                    </Form.Text>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Ингредиенты</Form.Label>
                    <Form.Control as="textarea" rows={3} {...register('ingredients')} />
                </Form.Group>
                <Form.Group>
                    <Form.File id="exampleFormControlFile1" label="Картинка" {...register('image')} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Вес</Form.Label>
                    <Form.Control type="text" placeholder="Введите вес" {...register('weight')} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Калорийность</Form.Label>
                    <Form.Control type="text" placeholder="Введите калорийность" {...register('calorie')} />
                </Form.Group>
                <div className={styles.buttons}>
                    <Button variant="primary" type="submit">
                        Добавить
                    </Button>
                    <Button variant="secondary" type="button">Отмена</Button>
                </div>
            </Form>
        </Popup>
    );
});
