import { isEqual } from '.';
export function remove(product, list) {
    for (let item of list) {
        console.log(item)
        if (isEqual(product, item)) {
            console.log('test')
            return list.filter((i) => !isEqual(i, item));
        } else if (Array.isArray(item.products)) {
            remove(product, item.products);
        }
    }
    return list;
}
