import React, { useRef, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";
import styles from "./PopupCropper.module.css";
import cn from "classnames";
import { Popup } from "../";
import { Form, Button } from "react-bootstrap";
import * as cropperSlice from "./PopupCropper.slice";
import PopupCropperPreview from './PopupCropperPreview/PopupCropperPreview';
const style = {
  width: 750,
  height: 470,
};

const options = {
  aspectRatio: 75 / 47,
  autoCropArea: 1,
  style,
};

export const PopupCropper = ({ register }) => {
  const [ cropper, setCropper ] = useState('');

  const cropperRef = useRef(null);
  const dispatch = useDispatch();
  const state = useSelector(cropperSlice.selectState);
  const image = useSelector(cropperSlice.selectImage);
  const url = useSelector(cropperSlice.selectUrl);

  const getCropData = () => {
    if (typeof cropper !== "undefined") {
      const croppImage = cropper.getCroppedCanvas().toDataURL();
      const formData = new FormData();
      const blob = new Blob([croppImage], { type: "image/png" });
      formData.append("image", blob);
      dispatch(cropperSlice.saveImageAsync(formData));
    }
  };

  const onClose = () => {
    dispatch(cropperSlice.handleClose());
  };

  const handleCropper = (file) => {
    const isImage = /^image\/\w+$/.test(file.type);
    if (!isImage) return;
    const reader = new FileReader();
    reader.onload = () => {
      dispatch(cropperSlice.handleOpen(reader.result));
    };
    reader.readAsDataURL(file);
  };

  const onChange = (e) => {
    e.preventDefault();
    let files;
    if (e.dataTransfer) {
      files = e.dataTransfer.files;
    } else if (e.target) {
      files = e.target.files;
    }
    if (files.length) {
      handleCropper(files[0]);
    }
  };

  return (
    <>
      <PopupCropperPreview url={url} />
      <Form.Group>
        <Form.File
          label="Картинка"
          {...register("image")}
          onChange={onChange}
        />
      </Form.Group>
      <Popup className={styles.popup} classContainer={styles.container} opened={state}>
        <Cropper
          src={image || ''}
          {...options}
          ref={cropperRef}
          onInitialized={(instance) => {
            setCropper(instance);
          }}
        />
        <div className={styles.buttons}>
          <Button onClick={getCropData}>Добавить</Button>
          <Button onClick={onClose} variant="secondary">Отмена</Button>
        </div>
      </Popup>
    </>
  );
};
