import { createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import { saveImage } from "../../services/api";

const initialState = {
  isOpen: false,
  image: "",
  url: "",
  loading: false,
};

export const saveImageAsync = createAsyncThunk(
  "tree/saveImage",
  async (data) => {
    const response = await saveImage(data);
    return response.data;
  }
);

export const popupCropperSlice = createSlice({
  name: "popupCropper",
  initialState,
  reducers: {
    handleClose: (state) => {
      state.isOpen = false;
      state.image = "";
    },
    handleOpen: (state, action) => {
      state.image = action.payload;
      state.isOpen = true;
      state.url = "";
    },
    handleRemove: () => {

    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(saveImageAsync.pending, (state) => {
        state.loading = true;
      })
      .addCase(saveImageAsync.fulfilled, (state, action) => {
        state.url = action.payload;
        state.loading = false;
        state.isOpen = false;
      });
  },
});

export const { handleClose, handleOpen } = popupCropperSlice.actions;

export const selectState = (state) => state.popupCropper.isOpen;
export const selectImage = (state) => state.popupCropper.image;
export const selectUrl = (state) => state.popupCropper.url;

export default popupCropperSlice.reducer;
