import React, { useState, useMemo, useEffect } from 'react';
import { Tree } from '../'
import { 
    testProductArray,
    flattenProducts,
} from '../../utils';
import { dishes } from '../../module';

export const App = () => {
    const [products, setProducts] = useState([]);
    const [list, setList] = useState([]);

    const tree = useMemo(() => {
        dishes.tree(products, list), [ products, list ]});
    console.log(tree)
    const handleStatus = (product) => {
        setProducts(dishes.setStatus(product, tree, !product.active));
    }

    const handleExpand = (product) => {
        console.log(dishes.set(product, tree, 'expanded', !product.expanded ));
        setProducts(dishes.set(product, tree, 'expanded', !product.expanded ));
    }

    useEffect(() => {
        const list = flattenProducts(testProductArray);
        const products = dishes.tree(testProductArray, list);
        setList(list);
        setProducts(products);
    }, [])

    return (
        <>
            {tree && tree.length ? 
                <Tree 
                    tree={products} 
                    handleStatus={handleStatus}
                    handleExpand={handleExpand}
                /> : 
                null
            }
        </>
    )
}
