import React from "react";
import { Button } from "react-bootstrap";
import styles from "./ImageCropperPreview.module.css";
import cn from 'classnames';
const ImageCropperPreview = ({ url = '', className, classImage, classMenu }) => {
  return (
    <>
     {(url) ? 
        <div className={cn(styles.preview, className)} >
            <img className={cn(styles.image, classImage)} src={url} alt="preview" />
            <div className={cn(styles.menu, classMenu)}>
                <Button className="mr-2">Изменить</Button>
                <Button className="mr-2" variant="danger">Удалить</Button>
            </div>
        </div>
        : null}
    </>
  );
};
export default PopupCropperPreview;
