import { isEqual } from '.';
export function remove(product, list) {
    for (let item of list) {
        if (isEqual(product.parent_item, item)) {
            return list.filter((i) => !isEqual(i, item));
        } else {
            remove(product, item.products);
        }
    }
    return list;
}
