import { isDishes } from ".";
export function isPassive(tree) {
  return tree.map((branch) => {
    if (isDishes(branch)) {
      branch.isPassive = branch.products.some((item) => item.active);
      branch.products = isPassive(branch.products);
    }
    return branch;
  });
}

export function getPassive(tree) {
  for (let branch of tree) {
    if (branch.category_id) {
      const allActiveProducts = branch.products.some((product) => product.active);
      const allActiveCategory = branch.children.some((category) => category.disabled);
      branch.isPassive = allActiveProducts && allActiveCategory
    }
  }
}
