import { createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import { saveImage } from "../../services/api";

const initialState = {
  isOpen: false,
  image: null,
};

export const saveImageAsync = createAsyncThunk(
  "tree/saveImage",
  async (amount) => {
    const response = await saveImage(amount);
    return response.data;
  }
);

export const popupCropperSlice = createSlice({
  name: "popupCropper",
  initialState,
  reducers: {
    handleClose: (state) => {
      state.isOpen = false;
      state.image = null;
    },
    handleOpen: (state, action) => {
      state.image = action.payload;
      state.isOpen = true;
    },
  },
});

export const { handleClose, handleOpen } = popupCropperSlice.actions;

export const selectState = (state) => state.popupCropper.isOpen;
export const selectImage = (state) => state.popupCropper.image;

export default popupCropperSlice.reducer;
