import React, { createRef, useEffect, memo } from 'react';
import styles from'./Tree.module.css';
import { Branch } from '../';
import cn from 'classnames';

export const Tree = memo(({ tree, className, expanded}) => {
    const treeElement = createRef(null);

    function getKey({ category_id, product_id, parent_id, }) {
        if (!parent_id) {
            return category_id
        } 
        if (!product_id) {
            return category_id;
        }
        return product_id;
    }
    const onSort = () => {
        console.log('1234')
    };
    const onEnd = () => {
        var itemEl = evt.item;  // dragged HTMLElement
        evt.to;    // target list
        evt.from;  // previous list
        evt.oldIndex;  // element's old index within old parent
        evt.newIndex;  // element's new index within new parent
        evt.oldDraggableIndex; // element's old index within old parent, only counting draggable elements
        evt.newDraggableIndex; // element's new index within new parent, only counting draggable elements
        evt.clone // the clone element
        evt.pullMode;
        console.log(itemEl)
        if (evt.to.id==="sTree2" && itemEl.type==="item") {
            alert("Ай бля");
            const nowTree = document.getElementById("sTree2");
            nowTree.replaceWith(stor);
            start();
        }
        console.log(evt.to, evt.from)
        console.log(evt.oldIndex, evt.newIndex)
        console.log(stor);
    };

    useEffect(() => {
        window.$(treeElement.current).sortable({ 
            // fallbackOnBody: false,
            group: 'list',
            animation: 200,
            ghostClass: 'ghost',
            handle: '.position',
            onSort: onSort,
        });
    }, [treeElement]);

    return (
        <ul ref={treeElement} className={cn(styles.tree, className, 'list-group', {
            [styles.open]: expanded,
        })}>
            {tree && tree.map((branch) => (
                <Branch 
                    key={getKey(branch)} 
                    product={branch}
                />
            ))}
        </ul>
    )
});
