import React, { useState, useMemo, useEffect } from 'react';
import { Tree } from '../'
import { 
    testProductArray,
    productsTree,
    flattenProducts
} from '../../utils';

export const App = () => {
    const [products, setProducts] = useState([]);
    const [list, setList] = useState([]);
    const items = useMemo(() => flattenProducts(list), [ list ]);
    const tree = useMemo(() => productsTree(products, items), [ products, items ]);
    console.log('render')

    useEffect(() => {
        const list = flattenProducts(testProductArray);
        setList(list);
    }, []);

    return (
        <>
            {tree && tree.length ? <Tree tree={products} /> : null}
        </>
    )
}
