import React, { useState, useEffect } from 'react';
import { Popup, Steps } from '..';
import styles from './PopupProduct.module.css';
import { useSelector, useDispatch } from "react-redux";
import { useForm, Controller } from "react-hook-form";
import {
  selectState,
  handleClose,
  selectData
} from "./PopupProductSlice";

import { OneStep, TwoStep, ThreeStep } from './Steps';

export const PopupProduct = () => {
    console.log('render PopupProduct')
    const [step, setStep] = useState(1);
    const [dataForm, setDataForm] = useState({});
    const state = useSelector(selectState);
    const data = useSelector(selectData);
    const dispatch = useDispatch();

    const {
        register,
        handleSubmit,
        formState: { errors },
        reset,
      } = useForm();


    const steps = [
        { value: 1 },
        { value: 2 },
        { value: 3 },
    ];

    const nextStep = (values) => {
        setDataForm({
            ...dataForm,
            ...values,
        });
        if (steps.length === step) {
            console.log(dataForm);
            return
        };
        setStep(step+1);
    };

    const onClose = () => {
        setDataForm({});
        dispatch(handleClose());
        setStep(1);
    };

    const backStep = () => {
        if (step === 1) {
            handleClose();
            return
        }; 
        setStep(step-1);
    };

    useEffect(() => {
        setDataForm(data);
    }, [data]);

    useEffect(() => {
        reset(data)
    }, [reset, data]);

    return (
        <Popup opened={state} title="Добавить продукт" onClose={onClose} classContainer={styles.popup}>
            <Steps className={styles.steps} steps={steps} step={step} />
            <OneStep step={1} data={dataForm} onSubmit={nextStep} onBack={backStep} />
            <TwoStep step={2} data={dataForm} onSubmit={nextStep} onBack={backStep} />
            <ThreeStep step={3} data={dataForm} onSubmit={nextStep} onBack={backStep} />
        </Popup>
    );
};
