import React, { createRef, useEffect } from 'react';
import styles from'./Tree.module.css';
import { Branch } from '../';
import cn from 'classnames';

export const Tree = ({ tree, view='open', className }) => {
    const treeElement = createRef(null);

    function getKey({ category_id, product_id, parent_id, }) {
        if (!parent_id) {
            return category_id
        } 
        if (!product_id) {
            return category_id;
        }
        return product_id;
    }

    useEffect(() => {
        window.$(treeElement.current).sortable({ 
            // fallbackOnBody: false,
            group: 'list',
            animation: 200,
            ghostClass: 'ghost',
            handle: '.position',
        });
    }, [treeElement]);

    return (
        <ul ref={treeElement} className={cn(styles.tree, className, 'list-group m-4', {
            [styles.open]: view === 'open'
        })}>
            {tree && tree.map((branch) => (
                <Branch key={getKey(branch)} {...branch} />
            ))}
        </ul>
    )
}
