import React, { useState, useEffect } from 'react';
import { Popup, Steps } from '..';
import styles from './PopupProduct.module.css';
import { useSelector, useDispatch } from "react-redux";
import { useForm, Controller } from "react-hook-form";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import {
  selectState,
  handleClose,
  selectData
} from "./PopupProductSlice";

import { OneStep, TwoStep, ThreeStep } from './Steps';

export const PopupProduct = () => {
    console.log('render PopupProduct');
    const [dataForm, setDataForm] = useState({});
    const state = useSelector(selectState);
    const data = useSelector(selectData);
    const dispatch = useDispatch();

    const {
        register,
        handleSubmit,
        formState: { errors },
        reset,
      } = useForm();


    const steps = [
        { value: 1 },
        { value: 2 },
        { value: 3 },
    ];

    const onClose = () => {
        setDataForm({});
        dispatch(handleClose());
    };

    useEffect(() => {
        setDataForm(data);
    }, [data]);

    useEffect(() => {
        reset(data)
    }, [reset, data]);

    return (
        <Popup opened={state} title="Добавить продукт" onClose={onClose} classContainer={styles.popup}>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <OneStep step={1} data={dataForm} />
                <TwoStep step={2} data={dataForm} />
                <ThreeStep step={3} data={dataForm} />
            </Form>
        </Popup>
    );
};
